import React from 'react';
import { Form, FormInput, FormGroup, Button, Card, CardBody, CardTitle, Progress } from "shards-react";


import {
    Table,
    Pagination,
    Row,
    Col,
    Divider,

} from 'antd'

import { getChannel } from '../fetcher'

const { Column, ColumnGroup } = Table;


class TopChannelsPage extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            channelQuery: "",
            queryResults: [],
        }
        this.handleChannelSearch = this.handleChannelSearch.bind(this)
    }



    handleChannelSearch(event) {
        this.setState({ channelQuery: event.target.value })
    }


    updateSearchResults() {
        //TASK 11: call getMatchSearch and update matchesResults in state. See componentDidMount() for a hint
        getChannel(this.state.channelQuery).then(res => {
            this.setState({ queryResults: res.results })
        })
    }

    componentDidMount() {
        getChannel(this.state.channelQuery).then(res => {
            this.setState({ queryResults: res.results })
        })
    }

    render() {
        return (
            <div>
                <h3>Hello! Type a name of the channel you would like to display below!</h3>
                <Form style={{ width: '80vw', margin: '0 auto', marginTop: '5vh' }}>
                    <Row>
                        <Col flex={2}><FormGroup style={{ width: '20vw', margin: '0 auto' }}>
                            <label>Channel Name</label>
                            <FormInput placeholder="Channel Name" value={this.state.channelQuery} onChange={this.handleChannelSearch} />
                        </FormGroup></Col>

                        <Col flex={2}><FormGroup style={{ width: '10vw' }}>
                            <Button style={{ marginTop: '4vh' }} onClick={this.updateSearchResults}>Click to Execute Search</Button>
                        </FormGroup></Col>
                    </Row>
                </Form>
            </div>
        )
    }
}

export default TopChannelsPage
